//импортируем стили
import "./App.css";

import { useState, useRef } from "react";
import { Content } from "./components/content";
import { Header } from "./components/header";
import { Footer } from "./components/footer";
import { DefaultContext } from "./context";
import { ThemeContext } from "./theme-context";

export default function App() {
  const theme = {
    light: "white",
    dark: "black"
  };

  const headerBackground = useRef(null);
  const contentBackground = useRef(null);

  const changeTheme = () => {
    let currentColor = headerBackground.style.backgroundColor;
    if(currentColor === "black") {
      headerBackground.style.backgroundColor = theme.light;
      contentBackground.style.backgroundColor = theme.light;
    }
    else {
      headerBackground.style.backgroundColor = theme.dark;
      contentBackground.style.backgroundColor = theme.dark;
    }
  };

  const [fan, setFan] = useState("");

  const handleCreateFan = ({name}) => {
      setFan(name);
  };

  const [selector, setSelector] = useState("Characters");

  const handleSelectorChange = (event) => {
      setSelector(event.target.value);
  };

  return (
    <DefaultContext.Provider value={{selector, handleSelectorChange, fan, handleCreateFan, changeTheme, headerBackground, contentBackground}}>
      <ThemeContext.Provider value={{theme}}>
        <Header />
        <hr />
        <Content />
      </ThemeContext.Provider>
      <Footer />
    </DefaultContext.Provider>
  );
}
